import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  username: string = ''; // Initialize as an empty string
  password: string = ''; // Initialize as an empty string
  errorMessage: string = ''; // Initialize as an empty string

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit() {
    this.authService.login(this.username, this.password).subscribe(
      (response) => {
        if (response.body && response.body.redirectUrl) {
          console.log('Login successful!'); // Log success message in the console
          this.router.navigate([response.body.redirectUrl]); // Redirect to home
        }
      },
      (error) => {
        this.errorMessage = 'Incorrect username or password';
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>TimeMaster Login</title>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600&display=swap" rel="stylesheet">
  </head>
  <body>
    <main>
      <section class="login-box">
        <img src="assets/logoTM.png" alt="TimeMaster Logo" class="logo" />
        <header>
          <h2>Welcome Back!</h2>
          <h3>To <span class="brand-name">TimeMaster</span></h3>
        </header>
        <form (ngSubmit)="onSubmit()">
          <input type="text" [(ngModel)]="username" name="username" placeholder="Username" class="input-field" required />
          <input type="password" [(ngModel)]="password" name="password" placeholder="Password" class="input-field" required />
          <button type="submit" class="login-btn">Login</button>
        </form>
        <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>

        <p class="register-text">Need assistance? <a href="#">Contact Administration</a></p>
        <nav class="social-media-icons">
          <a href="#"><i class="fab fa-facebook-f"></i></a>
          <a href="#"><i class="fab fa-x-twitter"></i></a> <!-- Changed to X (Twitter) -->
          <a href="#"><i class="fab fa-instagram"></i></a> <!-- Changed to Instagram -->
        </nav>
      </section>
      <aside class="illustration">
        <img src="assets/computer.png" alt="Computer Illustration" class="computer-image" />
      </aside>
    </main>


  </body>
</html>
