import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; // Importations pour le formulaire
import { Teacher } from 'src/app/models/teacher.model';
import { TeachersService } from 'src/app/services/teachers.service';

@Component({
  selector: 'app-teachers',
  templateUrl: './teachers.component.html',
  styleUrls: ['./teachers.component.css']
})
export class TeachersComponent implements OnInit {
  teachers: Teacher[] = [];
  isEditing: boolean = false;  // Pour activer le mode édition
  currentTeacherId: number | null = null;  // Pour garder le professeur en cours d'édition
  editTeacherForm: FormGroup;  // Formulaire d'édition
  selectedTeacher: Teacher | null = null;

  constructor(
    private teachersService: TeachersService,
    private fb: FormBuilder // Utilisation de FormBuilder pour créer des formulaires réactifs
  ) {
    // Initialisation du formulaire avec les attributs corrects
    this.editTeacherForm = this.fb.group({
      username: ['', Validators.required],
      maxDaysPerWeek: [null, Validators.required],
      maxHoursPerDay: [null, Validators.required],
      maxGapsPerDay: [null, Validators.required],
      subjectSpecialization: ['', Validators.required], // Assuming a string input for simplicity
      assignedActivities: ['', Validators.required]     // Assuming a string input for simplicity
    });
  }

  ngOnInit(): void {
    this.getTeachers();
  }

  getTeachers(): void {
    this.teachersService.getAllTeachers().subscribe((data: Teacher[]) => {
      this.teachers = data;
    });
  }

  deleteTeacher(id: number): void {
    this.teachersService.deleteTeacher(id).subscribe(
      () => {
        console.log('Teacher deleted successfully');
        this.teachers = this.teachers.filter(teacher => teacher.id !== id);
      },
      error => {
        console.log('Error deleting teacher', error);
      }
    );
  }

  // Démarrer l'édition d'un professeur
  startEditing(teacher: Teacher): void {
    this.isEditing = true;  // Active le mode édition
    this.currentTeacherId = teacher.id;  // Stocke l'ID du professeur à éditer

    // Remplir le formulaire avec les informations du professeur
    this.editTeacherForm.patchValue({
      name: teacher.username,
      maxDaysPerWeek: teacher.maxDaysPerWeek,
      maxHoursPerDay: teacher.maxHoursPerDay,
      maxGapsPerDay: teacher.maxGapsPerDay,
      subjectSpecialization: teacher.subjectSpecialization.join(', '), // Assuming comma-separated values for display
      assignedActivities: teacher.assignedActivities.join(', ')        // Assuming comma-separated values for display
    });
  }

  // Annuler l'édition
  cancelEditing(): void {
    this.isEditing = false;  // Désactive le mode édition
    this.currentTeacherId = null;
  }

  // Enregistrer les modifications
  updateTeacher(): void {
    if (this.editTeacherForm.valid && this.currentTeacherId !== null) {
      this.teachersService.updateTeacher(this.currentTeacherId, this.editTeacherForm.value).subscribe(
        () => {
          console.log('Teacher updated successfully');
          this.getTeachers();  // Recharge la liste des professeurs après la mise à jour
          this.isEditing = false;
          this.currentTeacherId = null;
        },
        error => {
          console.log('Error updating teacher', error);
        }
      );
    }
  }

  getTeacherDetails(id: number): void {
    this.teachersService.getTeacherById(id).subscribe(
      (teacher: Teacher) => {
        this.selectedTeacher = teacher; // Stocker les détails du professeur sélectionné
      },
      (error) => {
        console.log('Error fetching teacher details', error);
      }
    );
  }

  // Pour masquer les détails
  clearSelectedTeacher(): void {
    this.selectedTeacher = null;
  }
}
<div class="container">
    <!-- Display the list of teachers -->
    <div *ngIf="!isEditing; else editForm">
        <h1>Teachers List</h1>

        <!-- List of teachers -->
        <div *ngIf="teachers.length > 0; else noTeachers">
            <ul class="teacher-list">
                <li *ngFor="let teacher of teachers">
                    <span>{{ teacher.username }}</span> 
                    < <button class="detail-button" [routerLink]="['/teachers', teacher.id]">Informations</button> -->


                  <!--  <button class="edit-button" (click)="startEditing(teacher)">Edit</button> -->
                    <button class="delete-button" (click)="deleteTeacher(teacher.id)">Delete</button>
                </li>
            </ul>
        </div>

        <!-- Message if no teachers are available -->
        <ng-template #noTeachers>
            <p>No teachers available.</p>
        </ng-template>
    </div>

    <!-- Display selected teacher details -->
    <div *ngIf="selectedTeacher" class="teacher-details">
        <h2>Teacher Details</h2>
        <p><strong>Name:</strong> {{ selectedTeacher.username }}</p>
        <p><strong>Max Days Per Week:</strong> {{ selectedTeacher.maxDaysPerWeek }}</p>
        <p><strong>Max Hours Per Day:</strong> {{ selectedTeacher.maxHoursPerDay }}</p>
        <p><strong>Max Gaps Per Day:</strong> {{ selectedTeacher.maxGapsPerDay }}</p>
        <p><strong>Subject Specialization:</strong> {{ selectedTeacher.subjectSpecialization.join(', ') }}</p>
        <p><strong>Assigned Activities:</strong> {{ selectedTeacher.assignedActivities.join(', ') }}</p>
        <button (click)="clearSelectedTeacher()">Close</button>
    </div>

    <!-- Teacher Edit Form -->
    <ng-template #editForm>
        <h2>Edit Teacher</h2>
        <form [formGroup]="editTeacherForm" (ngSubmit)="updateTeacher()">
            <label for="name">Name</label>
            <input id="name" formControlName="name" />

            <label for="maxDaysPerWeek">Max Days Per Week</label>
            <input id="maxDaysPerWeek" formControlName="maxDaysPerWeek" type="number" />

            <label for="maxHoursPerDay">Max Hours Per Day</label>
            <input id="maxHoursPerDay" formControlName="maxHoursPerDay" type="number" />

            <label for="maxGapsPerDay">Max Gaps Per Day</label>
            <input id="maxGapsPerDay" formControlName="maxGapsPerDay" type="number" />

            <label for="subjectSpecialization">Subject Specialization</label>
            <input id="subjectSpecialization" formControlName="subjectSpecialization" />

            <label for="assignedActivities">Assigned Activities</label>
            <input id="assignedActivities" formControlName="assignedActivities" />

            <button type="submit">Update</button>
            <button type="button" (click)="cancelEditing()">Cancel</button>
        </form>
    </ng-template>
</div>
