import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; // Importations pour le formulaire
import { Student } from 'src/app/models/student.model';
import { StudentsService } from 'src/app/services/students.service';

@Component({
  selector: 'app-students',
  templateUrl: './students.component.html',
  styleUrls: ['./students.component.css']
})
export class StudentsComponent implements OnInit {
  students: Student[] = [];
  isEditing: boolean = false;  // Pour activer le mode édition
  currentStudentId: number | null = null;  // Pour garder l'étudiant en cours d'édition
  editStudentForm: FormGroup;  // Formulaire d'édition

  constructor(
    private studentsService: StudentsService,
    private fb: FormBuilder // Utilisation de FormBuilder pour créer des formulaires réactifs
  ) {
    // Initialisation du formulaire
    this.editStudentForm = this.fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required],
      role: ['', Validators.required],
      overlappingGroups: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.getStudents();
  }

  getStudents(): void {
    this.studentsService.getAllStudents().subscribe((data: Student[]) => {
      this.students = data;
    });
  }

  deleteStudent(id: number): void {
    this.studentsService.deleteStudent(id).subscribe(
      () => {
        console.log('Student deleted successfully');
        this.students = this.students.filter(student => student.id !== id);
      },
      error => {
        console.log('Error deleting student', error);
      }
    );
  }

  // Démarrer l'édition d'un étudiant
  startEditing(student: Student): void {
    this.isEditing = true;  // Active le mode édition
    this.currentStudentId = student.id;  // Stocke l'ID de l'étudiant à éditer

    // Remplir le formulaire avec les informations de l'étudiant
    this.editStudentForm.patchValue({
      username: student.username,
      password: student.password,
      role: student.role,
      overlappingGroups: student.overlappingGroups
    });
  }

  // Annuler l'édition
  cancelEditing(): void {
    this.isEditing = false;  // Désactive le mode édition
    this.currentStudentId = null;
  }

  // Enregistrer les modifications
  updateStudent(): void {
    if (this.editStudentForm.valid && this.currentStudentId !== null) {
      this.studentsService.updateStudent(this.currentStudentId, this.editStudentForm.value).subscribe(
        () => {
          console.log('Student updated successfully');
          this.getStudents();  // Recharge la liste des étudiants après la mise à jour
          this.isEditing = false;
          this.currentStudentId = null;
        },
        error => {
          console.log('Error updating student', error);
        }
      );
    }
  }
}
<div class="container">
    <h1>Students list</h1>

    <!-- Vérifie si on est en mode édition -->
    <div *ngIf="!isEditing; else editForm">

      <!-- Liste des étudiants -->
      <div *ngIf="students.length > 0; else noStudents">
        <ul class="student-list">
          <li *ngFor="let student of students">
            <span>{{ student.username }}</span>
            <button class="detail-button">Informations</button>
            <button class="edit-button" (click)="startEditing(student)">Edit</button>
            <button class="delete-button" (click)="deleteStudent(student.id)">Delete</button>
          </li>
        </ul>
      </div>

      <!-- Message à afficher si la liste est vide -->
      <ng-template #noStudents>
        <p>No students available.</p>
      </ng-template>
    </div>

    <!-- Formulaire d'édition -->
    <ng-template #editForm>
      <h2>Edit Student</h2>
      <form [formGroup]="editStudentForm" (ngSubmit)="updateStudent()">
        <label for="username">Username</label>
        <input id="username" formControlName="username" />

        <label for="password">Password</label>
        <input id="password" formControlName="password" type="password" />

        <label for="role">Role</label>
        <input id="role" formControlName="role" />

        <label for="overlappingGroups">Overlapping Groups</label>
        <input id="overlappingGroups" formControlName="overlappingGroups" />

        <button type="submit">Update</button>
        <button type="button" (click)="cancelEditing()">Cancel</button>
      </form>
    </ng-template>
</div>
